mapper.update:
		UPDATE COMMENTS 
    	SET CONTENT = #{content}, 
    		MODIFY_DATE = SYSDATE
		WHERE NO = #{no} 
			AND WRITER_NO = #{writerNo} 
			AND BOARD_NO = #{boardNo} 
			AND DEL_DATE IS NULL

		UPDATE COMMENTS
    		SET DEL_DATE = SYSDATE
		WHERE NO = #{no} 
			AND WRITER_NO = #{writerNo} 
			AND BOARD_NO = #{boardNo} 
			AND DEL_DATE IS NULL
