mapper.update:
UPDATE BOARD
SET DEL_DATE = SYSDATE
WHERE NO = #{no}

UPDATE BOARD
SET CATEGORY_NO = #{categoryNo}
, TITLE = #{title}
, CONTENT = #{content}
, SECRET_YN = #{secretYn}
, MODIFY_DATE = SYSDATE
WHERE NO = #{no} AND DEL_DATE IS NULL

mapper.select:
SELECT VISITOR_NUM 
FROM VISITOR 
WHERE BOARD_NO = #{boardNo}

SELECT REFERENCE_NO 
FROM RECOMMEND 
WHERE BOARD_NO = #{boardNo}
