mapper.select:
SELECT * FROM ( SELECT Q.* ,ROWNUM R FROM ( SELECT TITLE , CONTENT , CREATED_DATE , SECRET_YN FROM QNA WHERE DEL_DATE IS NULL AND
SECRET_YN = 'N'
WRITER_NO = #{writerNo}
AND (TITLE LIKE '%${searchValue}%' OR CONTENT LIKE '%${searchValue}%') ORDER BY
CASE WHEN TITLE LIKE '%${searchValue}%' THEN 1 ELSE 2 END,
CREATED_DATE DESC ) Q ) WHERE R BETWEEN #{startNo} AND #{endNo}
